import React, { useState } from 'react';
import { auth, provider, signInWithPopup } from './firebase';
import PaymentPage from './PaymentPage';
import './App.css';

function App() {
  // ✅ Hooks go inside the component
  const [user, setUser] = useState(null);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState(0);

  const [users, setUsers] = useState([]);
  const [ownerForm, setOwnerForm] = useState({ name: '', landSize: '', rent: '' });
  const [renterForm, setRenterForm] = useState({ name: '', requirement: '' });

  // ✅ Google Sign-in
  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      alert('Google sign-in failed');
      console.error(error);
    }
  };

  // ✅ Handle Owner form submit
  const handleOwnerSubmit = (e) => {
    e.preventDefault();
    setUsers([...users, { ...ownerForm, type: 'Owner' }]);
    setOwnerForm({ name: '', landSize: '', rent: '' });
  };

  // ✅ Handle Renter form submit
  const handleRenterSubmit = (e) => {
    e.preventDefault();
    setUsers([...users, { ...renterForm, type: 'Renter' }]);
    setRenterForm({ name: '', requirement: '' });
  };

  return (
    <div className="App">
      <h1>HelloServe</h1>

      {!user ? (
        <button onClick={handleGoogleSignIn}>Sign in with Google</button>
      ) : (
        <h2>Welcome, {user.displayName}</h2>
      )}

      <h3>Owner Form</h3>
      <form onSubmit={handleOwnerSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={ownerForm.name}
          onChange={(e) => setOwnerForm({ ...ownerForm, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Land Size"
          value={ownerForm.landSize}
          onChange={(e) => setOwnerForm({ ...ownerForm, landSize: e.target.value })}
        />
        <input
          type="text"
          placeholder="Rent"
          value={ownerForm.rent}
          onChange={(e) => setOwnerForm({ ...ownerForm, rent: e.target.value })}
        />
        <button type="submit">Submit</button>
      </form>

      <h3>Renter Form</h3>
      <form onSubmit={handleRenterSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={renterForm.name}
          onChange={(e) => setRenterForm({ ...renterForm, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Requirement"
          value={renterForm.requirement}
          onChange={(e) => setRenterForm({ ...renterForm, requirement: e.target.value })}
        />
        <button type="submit">Submit</button>
      </form>

      <h3>Users</h3>
      <ul>
        {users.map((u, index) => (
          <li key={index}>
            {u.type}: {u.name} {u.type === 'Owner' ? `(Land: ${u.landSize}, Rent: ${u.rent})` : `(Needs: ${u.requirement})`}
          </li>
        ))}
      </ul>

      <button onClick={() => { setShowPayment(true); setPaymentAmount(500); }}>
        Make Payment
      </button>

      {showPayment && <PaymentPage amount={paymentAmount} />}
    </div>
  );
}

export default App;
