import React, { useState, useEffect } from 'react';
import './App.css';
// Import Firebase (make sure these are properly configured in firebase.js)
import { auth, provider, signInWithPopup } from './firebase';
// Import PaymentPage component
import PaymentPage from './PaymentPage';

function App() {
  // State variables
  const [users, setUsers] = useState([]);
  const [parkingSpaces, setParkingSpaces] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [screen, setScreen] = useState('landing'); // landing, owner, renter

  // Firebase user state
  const [user, setUser] = useState(null);

  // Payment states
  const [showPayment, setShowPayment] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState(0);

  // Owner form states
  const [ownerForm, setOwnerForm] = useState({
    name: '',
    email: '',
    size: '',
    gate: false,
    evCharging: false,
    shed: false,
    cctv: false,
    location: '',
    price: '',
    image: null
  });

  // Renter form states
  const [renterForm, setRenterForm] = useState({
    name: '',
    email: '',
    phone: '',
    carType: '',
    date: '',
    startTime: '',
    endTime: '',
    price: 0,
    image: null
  });

  // API URL - Make sure this matches your backend port
  const API_URL = 'http://localhost:8081';

  // Firebase Google Sign-In handler
  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      console.log('Google sign-in successful:', result.user);
    } catch (error) {
      console.error('Google sign-in failed:', error);
      alert('Google sign-in failed: ' + error.message);
    }
  };

  // Helper for price calculation
  const calculatePrice = () => {
    if (!renterForm.startTime || !renterForm.endTime || !renterForm.date) return 0;

    const start = new Date(`${renterForm.date}T${renterForm.startTime}`);
    const end = new Date(`${renterForm.date}T${renterForm.endTime}`);
    const diffMs = end - start;
    const diffHours = diffMs > 0 ? diffMs / (1000 * 60 * 60) : 0;

    // Fixed price per hour (you can make this dynamic later)
    const pricePerHour = 50;
    return Math.round(diffHours * pricePerHour * 100) / 100;
  };

  // Fetch data from backend
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch users
      try {
        const usersResponse = await fetch(`${API_URL}/api/users`);
        if (usersResponse.ok) {
          const usersData = await usersResponse.json();
          setUsers(usersData);
        }
      } catch (err) {
        console.warn('Could not fetch users:', err.message);
      }

      // Fetch parking spaces
      try {
        const spacesResponse = await fetch(`${API_URL}/api/parking-spaces`);
        if (spacesResponse.ok) {
          const spacesData = await spacesResponse.json();
          setParkingSpaces(spacesData);
        }
      } catch (err) {
        console.warn('Could not fetch parking spaces:', err.message);
      }

      // Fetch bookings
      try {
        const bookingsResponse = await fetch(`${API_URL}/api/bookings`);
        if (bookingsResponse.ok) {
          const bookingsData = await bookingsResponse.json();
          setBookings(bookingsData);
        }
      } catch (err) {
        console.warn('Could not fetch bookings:', err.message);
      }

    } catch (err) {
      setError(err.message);
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  // Handle owner form submission
  const handleOwnerSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('name', ownerForm.name);
      formData.append('email', ownerForm.email);
      formData.append('address', ownerForm.location); // Map location to address
      formData.append('status', 'AVAILABLE');
      formData.append('pricePerHour', ownerForm.price);
      formData.append('parkingType', ownerForm.shed ? 'COVERED' : 'OPEN');
      formData.append('maxVehicleSize', 'CAR');

      // Create amenities array
      const amenities = [];
      if (ownerForm.gate) amenities.push('GATE');
      if (ownerForm.evCharging) amenities.push('EV_CHARGING');
      if (ownerForm.shed) amenities.push('COVERED');
      if (ownerForm.cctv) amenities.push('CCTV');

      formData.append('amenities', JSON.stringify(amenities));
      formData.append('description', `Parking space of ${ownerForm.size} sq ft`);

      if (ownerForm.image) {
        formData.append('image', ownerForm.image);
      }

      const response = await fetch(`${API_URL}/api/parking-spaces`, {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        alert('Parking space registered successfully!');
        // Reset form
        setOwnerForm({
          name: '', email: '', size: '', gate: false, evCharging: false,
          shed: false, cctv: false, location: '', price: '', image: null
        });
        // Refresh data
        fetchData();
      } else {
        const errorText = await response.text();
        console.error('Server error:', errorText);
        alert(`Failed to register parking space: ${response.status}`);
      }
    } catch (error) {
      console.error('Error registering parking space:', error);
      alert('Error connecting to backend: ' + error.message);
    }
  };

  // Handle renter form submission
  const handleRenterSubmit = async (e) => {
    e.preventDefault();

    const price = calculatePrice();
    if (price <= 0) {
      alert('Please select valid start and end times');
      return;
    }

    setPaymentAmount(price);

    try {
      const formData = new FormData();
      formData.append('customerName', renterForm.name);
      formData.append('customerEmail', renterForm.email);
      formData.append('customerPhone', renterForm.phone);
      formData.append('vehicleType', renterForm.carType);
      formData.append('bookingDate', renterForm.date);
      formData.append('startTime', renterForm.startTime);
      formData.append('endTime', renterForm.endTime);
      formData.append('totalPrice', price);
      formData.append('status', 'PENDING');

      // For demo, use first available parking space or create a default one
      if (parkingSpaces.length > 0) {
        formData.append('parkingSpaceId', parkingSpaces[0].id);
      } else {
        formData.append('parkingSpaceId', '1'); // Default parking space ID
      }

      if (renterForm.image) {
        formData.append('vehicleImage', renterForm.image);
      }

      const response = await fetch(`${API_URL}/api/bookings`, {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        console.log('Booking created successfully');
        setShowPayment(true);
      } else {
        const errorText = await response.text();
        console.error('Server error:', errorText);
        alert(`Failed to create booking: ${response.status}`);
      }
    } catch (error) {
      console.error('Error creating booking:', error);
      alert('Error connecting to backend: ' + error.message);
    }
  };

  // Handle payment completion
  const handlePaymentComplete = () => {
    alert('Payment successful! Your parking space has been booked.');
    // Reset forms and go back to landing
    setRenterForm({
      name: '', email: '', phone: '', carType: '', date: '',
      startTime: '', endTime: '', price: 0, image: null
    });
    setShowPayment(false);
    setScreen('landing');
    fetchData(); // Refresh data
  };

  // Handle back button
  const handleBack = () => {
    if (showPayment) {
      setShowPayment(false);
    } else {
      setScreen('landing');
    }
  };

  // Loading state
  if (loading) {
    return (
      <div className="App">
        <div className="loading-spinner">
          <h2>Loading...</h2>
          <p>Connecting to HelloServe...</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="App">
        <div className="error-message">
          <h1>Connection Error</h1>
          <p>Cannot connect to backend: {error}</p>
          <p>Make sure your Spring Boot server is running on port 8081</p>
          <button onClick={fetchData} className="submit-btn">Retry Connection</button>
        </div>
      </div>
    );
  }

  // Main App Render
  return (
    <div className="App">
      {/* Landing Page */}
      {screen === 'landing' && (
        <div className="landing-page">
          <h1 className="landing-title">Welcome to HelloServe</h1>
          <p className="landing-subtitle">Your Smart Parking Solution</p>

          {/* Google Sign-In */}
          <button className="submit-btn google-signin" onClick={handleGoogleSignIn}>
             Sign in with Google
          </button>

          {/* Display signed-in user */}
          {user && (
            <div className="user-info">
              <p>Welcome, {user.displayName}!</p>
              <p className="user-email">{user.email}</p>
            </div>
          )}

          {/* Role Selection */}
          <div className="role-selection">
            <div className="role-block owner-block" onClick={() => setScreen('owner')}>
              <h2> Owner</h2>
              <p>List your parking space and earn money</p>
            </div>
            <div className="role-block renter-block" onClick={() => setScreen('renter')}>
              <h2> Renter</h2>
              <p>Find and book a parking space</p>
            </div>
          </div>

          {/* Stats Display */}
          <div className="stats">
            <div className="stat-item">
              <span className="stat-number">{parkingSpaces.length}</span>
              <span className="stat-label">Available Spaces</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{bookings.length}</span>
              <span className="stat-label">Active Bookings</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{users.length}</span>
              <span className="stat-label">Registered Users</span>
            </div>
          </div>
        </div>
      )}

      {/* Owner Registration Form */}
      {screen === 'owner' && (
        <div className="form-section">
          <button className="back-btn" onClick={handleBack}> Back</button>
          <h2> Parking Space Owner Registration</h2>
          <form className="form" onSubmit={handleOwnerSubmit}>
            <div className="form-group">
              <label>Full Name *</label>
              <input 
                type="text" 
                value={ownerForm.name} 
                onChange={e => setOwnerForm({...ownerForm, name: e.target.value})} 
                required 
                placeholder="Enter your full name"
              />
            </div>

            <div className="form-group">
              <label>Email Address *</label>
              <input 
                type="email" 
                value={ownerForm.email} 
                onChange={e => setOwnerForm({...ownerForm, email: e.target.value})} 
                required 
                placeholder="your@email.com"
              />
            </div>

            <div className="form-group">
              <label>Parking Size (sq ft) *</label>
              <input 
                type="number" 
                value={ownerForm.size} 
                onChange={e => setOwnerForm({...ownerForm, size: e.target.value})} 
                required 
                min="50"
                placeholder="e.g., 200"
              />
            </div>

            <div className="form-group">
              <label>Location/Address *</label>
              <input 
                type="text" 
                value={ownerForm.location} 
                onChange={e => setOwnerForm({...ownerForm, location: e.target.value})} 
                required 
                placeholder="Full address with landmark"
              />
            </div>

            <div className="form-group">
              <label>Price per Hour () *</label>
              <input 
                type="number" 
                value={ownerForm.price} 
                onChange={e => setOwnerForm({...ownerForm, price: e.target.value})} 
                required 
                min="10"
                placeholder="e.g., 50"
              />
            </div>

            {/* Amenities Checkboxes */}
            <div className="form-group amenities-group">
              <label>Available Amenities</label>
              <div className="checkbox-group">
                <label className="checkbox-label">
                  <input 
                    type="checkbox" 
                    checked={ownerForm.gate} 
                    onChange={e => setOwnerForm({...ownerForm, gate: e.target.checked})} 
                  />
                   Gate Available
                </label>

                <label className="checkbox-label">
                  <input 
                    type="checkbox" 
                    checked={ownerForm.evCharging} 
                    onChange={e => setOwnerForm({...ownerForm, evCharging: e.target.checked})} 
                  />
                   EV Charging Port
                </label>

                <label className="checkbox-label">
                  <input 
                    type="checkbox" 
                    checked={ownerForm.shed} 
                    onChange={e => setOwnerForm({...ownerForm, shed: e.target.checked})} 
                  />
                   Covered/Shed
                </label>

                <label className="checkbox-label">
                  <input 
                    type="checkbox" 
                    checked={ownerForm.cctv} 
                    onChange={e => setOwnerForm({...ownerForm, cctv: e.target.checked})} 
                  />
                   CCTV Security
                </label>
              </div>
            </div>

            <div className="form-group">
              <label>Parking Space Image *</label>
              <input 
                type="file" 
                accept="image/*" 
                onChange={e => setOwnerForm({...ownerForm, image: e.target.files[0]})} 
                required 
              />
              <small>Upload a clear photo of your parking space</small>
            </div>

            <button type="submit" className="submit-btn">Register Parking Space</button>
          </form>
        </div>
      )}

      {/* Renter Registration Form */}
      {screen === 'renter' && !showPayment && (
        <div className="form-section">
          <button className="back-btn" onClick={handleBack}> Back</button>
          <h2> Book a Parking Space</h2>
          <form className="form" onSubmit={handleRenterSubmit}>
            <div className="form-group">
              <label>Full Name *</label>
              <input 
                type="text" 
                value={renterForm.name} 
                onChange={e => setRenterForm({...renterForm, name: e.target.value})} 
                required 
                placeholder="Enter your full name"
              />
            </div>

            <div className="form-group">
              <label>Email Address *</label>
              <input 
                type="email" 
                value={renterForm.email} 
                onChange={e => setRenterForm({...renterForm, email: e.target.value})} 
                required 
                placeholder="your@email.com"
              />
            </div>

            <div className="form-group">
              <label>Phone Number *</label>
              <input 
                type="tel" 
                value={renterForm.phone} 
                onChange={e => setRenterForm({...renterForm, phone: e.target.value})} 
                required 
                placeholder="10-digit phone number"
                pattern="[0-9]{10}"
              />
            </div>

            <div className="form-group">
              <label>Vehicle Type *</label>
              <select 
                value={renterForm.carType} 
                onChange={e => setRenterForm({...renterForm, carType: e.target.value})} 
                required
              >
                <option value="">Select vehicle type</option>
                <option value="Hatchback">Hatchback</option>
                <option value="Sedan">Sedan</option>
                <option value="SUV">SUV</option>
                <option value="Bike">Bike</option>
                <option value="Scooter">Scooter</option>
                <option value="Other">Other</option>
              </select>
            </div>

            <div className="form-group">
              <label>Parking Date *</label>
              <input 
                type="date" 
                value={renterForm.date} 
                onChange={e => setRenterForm({...renterForm, date: e.target.value})} 
                required 
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Start Time *</label>
                <input 
                  type="time" 
                  value={renterForm.startTime} 
                  onChange={e => setRenterForm({...renterForm, startTime: e.target.value})} 
                  required 
                />
              </div>

              <div className="form-group">
                <label>End Time *</label>
                <input 
                  type="time" 
                  value={renterForm.endTime} 
                  onChange={e => setRenterForm({...renterForm, endTime: e.target.value})} 
                  required 
                />
              </div>
            </div>

            <div className="form-group">
              <label>Vehicle Image *</label>
              <input 
                type="file" 
                accept="image/*" 
                onChange={e => setRenterForm({...renterForm, image: e.target.files[0]})} 
                required 
              />
              <small>Upload a photo of your vehicle</small>
            </div>

            {/* Price Display */}
            <div className="form-group price-display">
              <label>Total Price</label>
              <div className="price-amount">{calculatePrice()}</div>
              <small>Price calculated at 50 per hour</small>
            </div>

            <button type="submit" className="submit-btn" disabled={calculatePrice() <= 0}>
              Proceed to Payment ({calculatePrice()})
            </button>
          </form>
        </div>
      )}

      {/* Payment Page */}
      {screen === 'renter' && showPayment && (
        <div className="payment-section">
          <button className="back-btn" onClick={handleBack}> Back to Form</button>
          <PaymentPage 
            amount={paymentAmount} 
            onPaymentComplete={handlePaymentComplete}
            bookingDetails={{
              name: renterForm.name,
              email: renterForm.email,
              date: renterForm.date,
              startTime: renterForm.startTime,
              endTime: renterForm.endTime,
              vehicleType: renterForm.carType
            }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
