import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { auth, provider, signInWithPopup } from './firebase';
import LandingPage from './pages/LandingPage';
import OwnerRegistration from './pages/OwnerRegistration';
import CarOwnerRegistration from './pages/CarOwnerRegistration';
import PaymentPage from './pages/PaymentPage';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [paymentAmount, setPaymentAmount] = useState(0);
  const API_URL = 'http://localhost:8081';

  // Google sign-in
  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      alert('Google sign-in failed');
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage onSelectRole={(role) => window.location.href = `/${role}`} onGoogleSignIn={handleGoogleSignIn} user={user} />} />
        <Route path="/owner" element={<OwnerRegistration API_URL={API_URL} />} />
        <Route path="/renter" element={<CarOwnerRegistration API_URL={API_URL} onProceedToPayment={amt => { setPaymentAmount(amt); window.location.href = '/payment'; }} />} />
        <Route path="/payment" element={<PaymentPage amount={paymentAmount} onPay={() => alert('Payment successful! (Demo)')} />} />
      </Routes>
    </Router>
  );
}

export default App;
