import React, { useState, useEffect } from 'react';
import './App.css';
// FIX: Ensure signOutUser is imported here
import { auth, provider, signInWithPopup, signOutUser } from './firebase'; 
import PaymentPage from './PaymentPage';

function App() {
  // State variables
  const [users, setUsers] = useState([]);
  const [parkingSpaces, setParkingSpaces] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [screen, setScreen] = useState('landing'); 

  // Firebase user state
  const [user, setUser] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // NEW State for profile menu

  // Payment states
  const [showPayment, setShowPayment] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState(0);

  // Owner form states
  const [ownerForm, setOwnerForm] = useState({
    name: '', email: '', size: '', gate: false, evCharging: false,
    shed: false, cctv: false, location: '', price: '', image: null
  });

  // Renter form states
  const [renterForm, setRenterForm] = useState({
    name: '', email: '', phone: '', carType: '', date: '',
    startTime: '', endTime: '', price: 0, image: null
  });

  const API_URL = 'http://localhost:8081';

  // --- Auth & User Management ---

  // Check initial auth state
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
      fetchData(); 
    });
    return unsubscribe;
  }, []);

  // Firebase Google Sign-In handler
  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error('Google sign-in failed:', error);
      alert('Sign-in failed. Please check your connection or allow popups.');
    }
  };

  // Firebase Sign-Out handler (Modified to also close the menu)
  const handleSignOut = async () => {
    try {
      await signOutUser();
      setUser(null);
      setScreen('landing');
      setIsMenuOpen(false); // Close menu after sign out
    } catch (error) {
      alert('Failed to sign out.');
    }
  };

  // --- Data & API Handlers (Unchanged from previous versions) ---

  const calculatePrice = () => {
    if (!renterForm.startTime || !renterForm.endTime || !renterForm.date) return 0;
    const start = new Date(`${renterForm.date}T${renterForm.startTime}`);
    const end = new Date(`${renterForm.date}T${renterForm.endTime}`);
    const diffMs = end - start;
    const diffHours = diffMs > 0 ? diffMs / (1000 * 60 * 60) : 0;
    const pricePerHour = 50;
    return Math.round(diffHours * pricePerHour * 100) / 100;
  };

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      const [usersRes, spacesRes, bookingsRes] = await Promise.all([
          fetch(`${API_URL}/api/users`),
          fetch(`${API_URL}/api/parking-spaces`),
          fetch(`${API_URL}/api/bookings`)
      ]);
      const usersData = usersRes.ok ? await usersRes.json() : [];
      const spacesData = spacesRes.ok ? await spacesRes.json() : [];
      const bookingsData = bookingsRes.ok ? await bookingsRes.json() : [];
      setUsers(usersData);
      setParkingSpaces(spacesData);
      setBookings(bookingsData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleOwnerSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', ownerForm.name);
      formData.append('email', ownerForm.email);
      formData.append('address', ownerForm.location); 
      formData.append('status', 'AVAILABLE');
      formData.append('pricePerHour', ownerForm.price);
      formData.append('parkingType', ownerForm.shed ? 'COVERED' : 'OPEN');
      formData.append('maxVehicleSize', 'CAR');
      const amenities = [];
      if (ownerForm.gate) amenities.push('GATE');
      if (ownerForm.evCharging) amenities.push('EV_CHARGING');
      if (ownerForm.shed) amenities.push('COVERED');
      if (ownerForm.cctv) amenities.push('CCTV');
      formData.append('amenities', JSON.stringify(amenities));
      formData.append('description', `Parking space of ${ownerForm.size} sq ft`);
      if (ownerForm.image) { formData.append('image', ownerForm.image); }

      const response = await fetch(`${API_URL}/api/parking-spaces`, { method: 'POST', body: formData });
      if (response.ok) {
        alert('Parking space registered successfully!');
        setOwnerForm({ name: '', email: '', size: '', gate: false, evCharging: false, shed: false, cctv: false, location: '', price: '', image: null });
        fetchData();
        setScreen('landing');
      } else {
        const errorText = await response.text();
        alert(`Failed to register parking space. Error: ${errorText.substring(0, 50)}...`);
      }
    } catch (error) {
      alert('An error occurred during registration.');
    }
  };

  const handleRenterSubmit = async (e) => {
    e.preventDefault();
    const price = calculatePrice();
    if (price <= 0) { alert('Please select valid start and end times for booking.'); return; }
    setPaymentAmount(price);

    try {
        const formData = new FormData();
        formData.append('customerName', renterForm.name);
        formData.append('customerEmail', renterForm.email);
        formData.append('customerPhone', renterForm.phone);
        formData.append('vehicleType', renterForm.carType);
        formData.append('bookingDate', renterForm.date);
        formData.append('startTime', renterForm.startTime);
        formData.append('endTime', renterForm.endTime);
        formData.append('totalPrice', price);
        formData.append('status', 'PENDING');
        const parkingSpaceId = parkingSpaces.length > 0 ? parkingSpaces[0].id : '1'; 
        formData.append('parkingSpaceId', parkingSpaceId);
        if (renterForm.image) { formData.append('vehicleImage', renterForm.image); }

        const response = await fetch(`${API_URL}/api/bookings`, { method: 'POST', body: formData });

        if (response.ok) {
            const newBooking = await response.json(); 
            setRenterForm(prev => ({...prev, bookingId: newBooking.id || 'temp-id'})); 
            setShowPayment(true);
        } else {
            const errorText = await response.text();
            alert(`Failed to create booking: ${response.status}. ${errorText.substring(0, 50)}...`);
        }
    } catch (error) {
        alert('An error occurred while preparing your booking.');
    }
  };

  const handlePaymentComplete = () => {
    alert('Payment successful! Your parking space has been booked.');
    setRenterForm({ name: '', email: '', phone: '', carType: '', date: '', startTime: '', endTime: '', price: 0, image: null });
    setShowPayment(false);
    setScreen('landing');
    fetchData();
  };

  const handleBack = () => {
    if (showPayment) {
      setShowPayment(false);
    } else {
      setScreen('landing');
    }
  };

  // --- NEW: Profile Dropdown Component ---
  const ProfileDropdown = () => {
    if (!user) return null;

    return (
      <div className="profile-dropdown-container">
        <div className="profile-button" onClick={() => setIsMenuOpen(!isMenuOpen)}>
          <img 
            src={user.photoURL || 'https://via.placeholder.com/30'} 
            alt="Profile" 
            className="profile-pic-small" 
          />
          <span className="profile-name-small">{user.displayName || 'User Profile'}</span>
        </div>

        {isMenuOpen && (
          <div className="dropdown-menu">
            <p style={{color: 'var(--primary-color)'}}>{user.email}</p>
            <button className="sign-out-btn" onClick={handleSignOut}>Sign Out</button>
          </div>
        )}
      </div>
    );
  };

  // --- Render Functions for different screens ---

  if (loading) {
    return (
      <div className="App">
        <div className="status-screen loading-spinner">
          <h2>Initializing Application</h2>
          <p>Connecting to backend services...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <div className="status-screen error-message">
          <h1>Connection Error</h1>
          <p>Cannot connect to backend: **{error}**</p>
          <p>Please ensure your Spring Boot server is running on **port 8081**.</p>
          <button onClick={fetchData} className="submit-btn" style={{marginTop: '30px'}}>Retry Connection</button>
        </div>
      </div>
    );
  }

  const LandingScreen = () => (
    <div className="landing-page">
      <h1 className="landing-title">HelloServe</h1>
      <p className="landing-subtitle">The Professional Smart Parking and Space Leasing Platform.</p>

      {!user ? (
        <div className="auth-section">
          <button className="google-signin" onClick={handleGoogleSignIn}>
            <img src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg" alt="Google logo" />
            Sign in with Google
          </button>
        </div>
      ) : (
        <>
          <div className="role-selection">
            <div className="role-block" onClick={() => setScreen('owner')}>
              <h2>Property Owner</h2>
              <p>Monetize your unused space by listing your parking spot.</p>
            </div>
            <div className="role-block" onClick={() => setScreen('renter')}>
              <h2>Vehicle Renter</h2>
              <p>Secure and book a parking space instantly for your vehicle.</p>
            </div>
          </div>
        </>
      )}

      <div className="stats">
        <div className="stat-item">
          <span className="stat-number">{parkingSpaces.length}</span>
          <span className="stat-label">Listed Spaces</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{bookings.length}</span>
          <span className="stat-label">Total Bookings</span>
        </div>
        <div className="stat-item">
          <span className="stat-number">{users.length}</span>
          <span className="stat-label">Registered Users</span>
        </div>
      </div>
    </div>
  );

  const OwnerScreen = () => (
    <div className="form-container">
      <button className="back-btn" onClick={handleBack}>← Back to Role Select</button>
      <h2 className="section-title">Property Listing Submission</h2>
      <form className="form" onSubmit={handleOwnerSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label>Full Name *</label>
            <input type="text" value={ownerForm.name} onChange={e => setOwnerForm({...ownerForm, name: e.target.value})} required placeholder="Enter your full name"/>
          </div>
          <div className="form-group">
            <label>Email Address *</label>
            <input type="email" value={ownerForm.email} onChange={e => setOwnerForm({...ownerForm, email: e.target.value})} required placeholder="your@email.com"/>
          </div>
        </div>
        <div className="form-group">
          <label>Space Address/Location *</label>
          <input type="text" value={ownerForm.location} onChange={e => setOwnerForm({...ownerForm, location: e.target.value})} required placeholder="Full address of the parking space"/>
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>Size (sq ft) *</label>
            <input type="number" value={ownerForm.size} onChange={e => setOwnerForm({...ownerForm, size: e.target.value})} required min="50" placeholder="e.g., 200"/>
          </div>
          <div className="form-group">
            <label>Price per Hour (₹) *</label>
            <input type="number" value={ownerForm.price} onChange={e => setOwnerForm({...ownerForm, price: e.target.value})} required min="10" placeholder="e.g., 50"/>
          </div>
        </div>
        <div className="form-group amenities-group">
          <label>Available Amenities</label>
          <div className="checkbox-group">
            <label className="checkbox-label">
              <input type="checkbox" checked={ownerForm.gate} onChange={e => setOwnerForm({...ownerForm, gate: e.target.checked})} />
              Gated Access
            </label>
            <label className="checkbox-label">
              <input type="checkbox" checked={ownerForm.evCharging} onChange={e => setOwnerForm({...ownerForm, evCharging: e.target.checked})} />
              EV Charging
            </label>
            <label className="checkbox-label">
              <input type="checkbox" checked={ownerForm.shed} onChange={e => setOwnerForm({...ownerForm, shed: e.target.checked})} />
              Covered Parking
            </label>
            <label className="checkbox-label">
              <input type="checkbox" checked={ownerForm.cctv} onChange={e => setOwnerForm({...ownerForm, cctv: e.target.checked})} />
              CCTV Security
            </label>
          </div>
        </div>
        <div className="form-group">
          <label>Space Image *</label>
          <input type="file" accept="image/*" onChange={e => setOwnerForm({...ownerForm, image: e.target.files[0]})} required />
          <small>Upload a clear, high-resolution photo of the space.</small>
        </div>
        <button type="submit" className="submit-btn">Submit Space Listing</button>
      </form>
    </div>
  );

  const RenterScreen = () => (
    <div className="form-container">
      <button className="back-btn" onClick={handleBack}>← Back to Role Select</button>
      <h2 className="section-title">New Parking Booking</h2>
      <form className="form" onSubmit={handleRenterSubmit}>
        <div className="form-group">
          <label>Full Name *</label>
          <input type="text" value={renterForm.name} onChange={e => setRenterForm({...renterForm, name: e.target.value})} required placeholder="Renter's full name"/>
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>Email Address *</label>
            <input type="email" value={renterForm.email} onChange={e => setRenterForm({...renterForm, email: e.target.value})} required placeholder="your@email.com"/>
          </div>
          <div className="form-group">
            <label>Phone Number *</label>
            <input type="tel" value={renterForm.phone} onChange={e => setRenterForm({...renterForm, phone: e.target.value})} required placeholder="10-digit number" pattern="[0-9]{10}"/>
          </div>
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>Vehicle Type *</label>
            <select value={renterForm.carType} onChange={e => setRenterForm({...renterForm, carType: e.target.value})} required>
              <option value="">Select vehicle type</option>
              <option value="Hatchback">Hatchback/Sedan</option>
              <option value="SUV">SUV/Truck</option>
              <option value="Bike">Bike/Scooter</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div className="form-group">
            <label>Vehicle Image *</label>
            <input type="file" accept="image/*" onChange={e => setRenterForm({...renterForm, image: e.target.files[0]})} required/>
            <small>Upload a photo of your vehicle.</small>
          </div>
        </div>
        <div className="form-group">
          <label>Booking Date *</label>
          <input type="date" value={renterForm.date} onChange={e => setRenterForm({...renterForm, date: e.target.value})} required min={new Date().toISOString().split('T')[0]}/>
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>Start Time *</label>
            <input type="time" value={renterForm.startTime} onChange={e => setRenterForm({...renterForm, startTime: e.target.value})} required/>
          </div>
          <div className="form-group">
            <label>End Time *</label>
            <input type="time" value={renterForm.endTime} onChange={e => setRenterForm({...renterForm, endTime: e.target.value})} required/>
          </div>
        </div>
        <div className="form-group price-display">
          <label style={{color: 'var(--text-primary)', marginBottom: '5px'}}>Estimated Total Price</label>
          <div className="price-amount">₹{calculatePrice()}</div>
          <small style={{color: 'var(--text-primary)'}}>Rate: ₹50 per hour</small>
        </div>
        <button type="submit" className="submit-btn" disabled={calculatePrice() <= 0}>
          Proceed to Payment (₹{calculatePrice()})
        </button>
      </form>
    </div>
  );

  const PaymentScreen = () => (
    <div className="form-container">
      <button className="back-btn" onClick={handleBack}>← Back to Booking Form</button>
      <h2 className="section-title">Secure Payment</h2>
      <div className="payment-content" style={{textAlign: 'center', padding: '20px'}}>
        <PaymentPage 
          amount={paymentAmount} 
          onPay={handlePaymentComplete} 
          bookingDetails={{
            name: renterForm.name,
            email: renterForm.email,
            date: renterForm.date,
            startTime: renterForm.startTime,
            endTime: renterForm.endTime,
            vehicleType: renterForm.carType
          }}
        />
        <p style={{marginTop: '30px', fontSize: '0.9em', color: 'var(--text-secondary)'}}>
          Note: This is a secure demonstration gateway. Clicking "Complete Payment" simulates a successful transaction.
        </p>
      </div>
    </div>
  );

  // --- Main Render Logic ---
  return (
    <div className="App">
      {/* FIX: Render the ProfileDropdown unconditionally if a user is logged in. 
        It manages its own position in the top-right corner via CSS. */}
      {user && <ProfileDropdown />}

      {screen === 'landing' && <LandingScreen />}
      {screen === 'owner' && <OwnerScreen />}
      {screen === 'renter' && !showPayment && <RenterScreen />}
      {screen === 'renter' && showPayment && <PaymentScreen />}
    </div>
  );
}

export default App;
