import { auth, provider, signInWithPopup } from './firebase';
  const [user, setUser] = useState(null);

  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      alert('Google sign-in failed');
    }
  };
import PaymentPage from './PaymentPage';
  const [showPayment, setShowPayment] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState(0);
import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [users, setUsers] = useState([]);
  const [parkingSpaces, setParkingSpaces] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [screen, setScreen] = useState('landing'); // landing, owner, renter

  // Owner form states
  const [ownerForm, setOwnerForm] = useState({
    name: '',
    email: '',
    size: '',
    gate: false,
    evCharging: false,
    shed: false,
    cctv: false,
    location: '',
    price: '',
    image: null
  });
  // Renter form states
  const [renterForm, setRenterForm] = useState({
    name: '',
    email: '',
    phone: '',
    carType: '',
    date: '',
    startTime: '',
    endTime: '',
    price: 0,
    image: null
  });

  // Helper for price calculation
  const calculatePrice = () => {
    if (!renterForm.startTime || !renterForm.endTime || !renterForm.date) return 0;
    const start = new Date(`${renterForm.date}T${renterForm.startTime}`);
    const end = new Date(`${renterForm.date}T${renterForm.endTime}`);
    const diffMs = end - start;
    const diffHours = diffMs > 0 ? diffMs / (1000 * 60 * 60) : 0;
    // For demo, use a fixed price per hour (e.g., 50). Replace with dynamic value if available.
    const pricePerHour = 50;
    return Math.round(diffHours * pricePerHour * 100) / 100;
  };

  // FIXED: Point to the backend server
  const API_URL = 'http://localhost:8081';

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);

      const usersResponse = await fetch(`${API_URL}/api/users`);
      if (!usersResponse.ok) throw new Error('Failed to fetch users');
      const usersData = await usersResponse.json();
      setUsers(usersData);

      const spacesResponse = await fetch(`${API_URL}/api/parking-spaces`);
      if (spacesResponse.ok) {
        const spacesData = await spacesResponse.json();
        setParkingSpaces(spacesData);
      }

      const bookingsResponse = await fetch(`${API_URL}/api/bookings`);
      if (bookingsResponse.ok) {
        const bookingsData = await bookingsResponse.json();
        setBookings(bookingsData);
      }

    } catch (err) {
      setError(err.message);
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  const addUser = async (e) => {
    e.preventDefault();
    try {
      console.log('Sending user data:', newUser);

      const formData = new FormData();
      formData.append('name', newUser.name);
      formData.append('email', newUser.email);
      formData.append('password', newUser.password);
      formData.append('role', newUser.role);

      if (newUser.role === 'USER' && newUser.vehicleType) {
        formData.append('vehicleType', newUser.vehicleType);
        if (newUser.vehicleImage) {
          formData.append('vehicleImage', newUser.vehicleImage);
        }
      }

      const response = await fetch(`${API_URL}/api/users/register`, {
        method: 'POST',
        body: formData, // Remove Content-Type header for FormData
      });

      console.log('Response status:', response.status);

      if (response.ok) {
        setNewUser({ name: '', email: '', password: '', role: 'USER', vehicleType: '', vehicleImage: null });
        fetchData(); // Refresh data
        alert('User added successfully!');
      } else {
        const errorText = await response.text();
        console.error('Server error:', errorText);
        alert(`Failed to add user: ${response.status} - ${errorText}`);
      }
    } catch (error) {
      console.error('Network error adding user:', error);
      alert(`Network error adding user: ${error.message}`);
    }
  };

  const addParkingSpace = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('address', newSpace.address);
      formData.append('status', newSpace.status);
      formData.append('ownerId', newSpace.ownerId);
      formData.append('pricePerHour', newSpace.pricePerHour);
      formData.append('parkingType', newSpace.parkingType);
      formData.append('maxVehicleSize', newSpace.maxVehicleSize);
      formData.append('description', newSpace.description);
      formData.append('amenities', JSON.stringify(newSpace.amenities));

      if (newSpace.image) {
        formData.append('image', newSpace.image);
      }

      const response = await fetch(`${API_URL}/api/parking-spaces`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setNewSpace({ 
          address: '', 
          status: 'AVAILABLE', 
          ownerId: '', 
          pricePerHour: '', 
          parkingType: 'COVERED',
          maxVehicleSize: 'CAR',
          amenities: [],
          description: '',
          image: null 
        });
        fetchData(); // Refresh data
        alert('Parking space added successfully!');
      } else {
        const errorText = await response.text();
        alert(`Failed to add parking space: ${response.status} - ${errorText}`);
      }
    } catch (error) {
      console.error('Error adding parking space:', error);
      alert('Error adding parking space');
    }
  };

  if (loading) {
    return <div className="App">Loading...</div>;
  }

  if (error) {
    return (
      <div className="App">
        <h1>Connection Error</h1>
        <p>Cannot connect to backend: {error}</p>
        <p>Make sure your Spring Boot server is running on port 8081</p>
        <button onClick={fetchData}>Retry</button>
      </div>
    );
  }

  return (
    <div className="App">
      {screen === 'landing' && (
        <div className="landing-page">
          <h1 className="landing-title">Welcome to HelloServe</h1>
          <button className="submit-btn" style={{marginBottom: 32}} onClick={handleGoogleSignIn}>
            Sign in with Google
          </button>
          {user && (
            <div style={{color: '#fff', marginBottom: 24}}>
              Signed in as: {user.displayName} ({user.email})
            </div>
          )}
          <div className="role-selection">
            <div className="role-block owner-block" onClick={() => setScreen('owner')}>
              <h2>Owner</h2>
              <p>List your parking space and earn</p>
            </div>
            <div className="role-block renter-block" onClick={() => setScreen('renter')}>
              <h2>Renter</h2>
              <p>Find and book a parking space</p>
            </div>
          </div>
        </div>
      )}

      {screen === 'owner' && (
        <div className="form-section">
          <h2>Parking Space Owner Registration</h2>
          <form className="form" onSubmit={async e => {
            e.preventDefault();
            const formData = new FormData();
            formData.append('name', ownerForm.name);
            formData.append('email', ownerForm.email);
            formData.append('size', ownerForm.size);
            formData.append('gate', ownerForm.gate);
            formData.append('evCharging', ownerForm.evCharging);
            formData.append('shed', ownerForm.shed);
            formData.append('cctv', ownerForm.cctv);
            formData.append('location', ownerForm.location);
            formData.append('price', ownerForm.price);
            if (ownerForm.image) formData.append('image', ownerForm.image);
            try {
              const response = await fetch(`${API_URL}/api/parking-spaces`, {
                method: 'POST',
                body: formData
              });
              if (response.ok) {
                alert('Parking space registered successfully!');
                setOwnerForm({ name: '', email: '', size: '', gate: false, evCharging: false, shed: false, cctv: false, location: '', price: '', image: null });
              } else {
                alert('Failed to register parking space');
              }
            } catch (err) {
              alert('Error connecting to backend');
            }
          }}>
            <div className="form-group">
              <label>Name:</label>
              <input type="text" value={ownerForm.name} onChange={e => setOwnerForm({...ownerForm, name: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Email:</label>
              <input type="email" value={ownerForm.email} onChange={e => setOwnerForm({...ownerForm, email: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Parking Size (sq ft):</label>
              <input type="number" value={ownerForm.size} onChange={e => setOwnerForm({...ownerForm, size: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Gate Available:</label>
              <input type="checkbox" checked={ownerForm.gate} onChange={e => setOwnerForm({...ownerForm, gate: e.target.checked})} />
            </div>
            <div className="form-group">
              <label>EV Charging Port:</label>
              <input type="checkbox" checked={ownerForm.evCharging} onChange={e => setOwnerForm({...ownerForm, evCharging: e.target.checked})} />
            </div>
            <div className="form-group">
              <label>Shed for Cover:</label>
              <input type="checkbox" checked={ownerForm.shed} onChange={e => setOwnerForm({...ownerForm, shed: e.target.checked})} />
            </div>
            <div className="form-group">
              <label>CCTV Available:</label>
              <input type="checkbox" checked={ownerForm.cctv} onChange={e => setOwnerForm({...ownerForm, cctv: e.target.checked})} />
            </div>
            <div className="form-group">
              <label>Location:</label>
              <input type="text" value={ownerForm.location} onChange={e => setOwnerForm({...ownerForm, location: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Price per Hour (₹):</label>
              <input type="number" value={ownerForm.price} onChange={e => setOwnerForm({...ownerForm, price: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Parking Space Image:</label>
              <input type="file" accept="image/*" onChange={e => setOwnerForm({...ownerForm, image: e.target.files[0]})} required />
            </div>
            <button type="submit" className="submit-btn">Submit</button>
          </form>
        </div>
      )}

      {screen === 'renter' && !showPayment && (
        <div className="form-section">
          <h2>Car Owner Registration</h2>
          <form className="form" onSubmit={async e => {
            e.preventDefault();
            const price = calculatePrice();
            setPaymentAmount(price);
            // Send booking to backend
            const formData = new FormData();
            formData.append('name', renterForm.name);
            formData.append('email', renterForm.email);
            formData.append('phone', renterForm.phone);
            formData.append('carType', renterForm.carType);
            formData.append('date', renterForm.date);
            formData.append('startTime', renterForm.startTime);
            formData.append('endTime', renterForm.endTime);
            formData.append('price', price);
            if (renterForm.image) formData.append('carImage', renterForm.image);
            try {
              const response = await fetch(`${API_URL}/api/bookings`, {
                method: 'POST',
                body: formData
              });
              if (response.ok) {
                setShowPayment(true);
              } else {
                alert('Failed to create booking');
              }
            } catch (err) {
              alert('Error connecting to backend');
            }
          }}>
            <div className="form-group">
              <label>Name:</label>
              <input type="text" value={renterForm.name} onChange={e => setRenterForm({...renterForm, name: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Email:</label>
              <input type="email" value={renterForm.email} onChange={e => setRenterForm({...renterForm, email: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Phone Number:</label>
              <input type="tel" value={renterForm.phone} onChange={e => setRenterForm({...renterForm, phone: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Car Type:</label>
              <input type="text" value={renterForm.carType} onChange={e => setRenterForm({...renterForm, carType: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Date:</label>
              <input type="date" value={renterForm.date} onChange={e => setRenterForm({...renterForm, date: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Start Time:</label>
              <input type="time" value={renterForm.startTime} onChange={e => setRenterForm({...renterForm, startTime: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>End Time:</label>
              <input type="time" value={renterForm.endTime} onChange={e => setRenterForm({...renterForm, endTime: e.target.value})} required />
            </div>
            <div className="form-group">
              <label>Car Image:</label>
              <input type="file" accept="image/*" onChange={e => setRenterForm({...renterForm, image: e.target.files[0]})} required />
            </div>
            <div className="form-group">
              <label>Price (₹):</label>
              <input type="number" value={calculatePrice()} readOnly />
              <small style={{color: '#764ba2'}}>Calculated in real-time based on selected time and date</small>
            </div>
            <button type="submit" className="submit-btn">Proceed to Payment</button>
          </form>
        </div>
      )}

      {screen === 'renter' && showPayment && (
        <PaymentPage amount={paymentAmount} onPay={() => alert('Payment successful! (Demo)')} />
      )}
    </div>
  );
}

export default App;
